package LeetCode;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;

public class L_438_FindAllAnagramsInAString {
    public List<Integer>findAnagrams1(String s, String p) {
        int left=0;
        int right=0;
        int n=s.length();
        List<Integer>result=new ArrayList<>();
        HashMap<Character,Integer>patternMap=getPatternMap(p);
        HashMap<Character,Integer>tempMap=new HashMap<>();
        while(right<n){
            char ch=s.charAt(right);
            tempMap.put(ch,tempMap.getOrDefault(ch,0)+1);
            if(right-left+1==p.length()){
                if(patternMap.equals(tempMap)){
                    result.add(left);
                }
                char leftChar=s.charAt(left);
                tempMap.put(leftChar,tempMap.getOrDefault(leftChar,0)-1);
                if(tempMap.get(leftChar)==0){
                    tempMap.remove(leftChar);
                }
                left++;
            }
            right++;
        }
        return result;
    }
    public HashMap<Character,Integer>getPatternMap(String p){
        HashMap<Character,Integer>patternMap=new HashMap<>();
        for(int i=0;i<p.length();i++){
            char ch=p.charAt(i);
            patternMap.put(ch,patternMap.getOrDefault(ch,0)+1);
        }
        return patternMap;
    }

    public static void main(String[] args) {
        L_438_FindAllAnagramsInAString anag=new L_438_FindAllAnagramsInAString();
        String s= "";
        String p="vxqakfyaqahufxfizupjrkkifjlbfqfeprqrfjvxnubntdtlvz";
        System.out.println(anag.findAnagrams1(s,p));
    }

    //=================================================================
        //Time Limit Exceeded
        public List<Integer> findAnagrams(String s, String p) {
        HashMap<Character,Integer> pmap=new HashMap<>();
        for(int i=0;i<p.length();i++){
            char ch=p.charAt(i);
            pmap.put(ch,pmap.getOrDefault(ch,0)+1);
        }
        List<Integer>result=new ArrayList<>();
        int start=0;
        int end=p.length();
        int n=s.length();
        while(end<=n){
            String word=s.substring(start,end);
            if(isAnagram(word,pmap)){
                result.add(start);
            }
            start++;
            end++;
        }
        return result;
    }

    public boolean isAnagram(String word, HashMap<Character,Integer>pmap){
        HashMap<Character,Integer>wmap=new HashMap<>();
        for(int i=0;i<word.length();i++){
            char ch=word.charAt(i);
            wmap.put(ch,wmap.getOrDefault(ch,0)+1);
        }
        return pmap.equals(wmap);
}
}
